INSERT INTO itemDetail(categoryId,subCategoryId,itemId,item,donateAndReuse,curbSidePickup,transferStation,inGarbage,reuse,dropOffLocations,hazWasteFacility,hazNoCharge,donate,warning,caution,inRecycling,didYouKnow,inFoodAndYardWaste,inRecyclingRinseFirst,nextToBinNoCharge,corporateTakeBackPrograms,pickup,hazardous,nextToBin,recycleNextToBinNoCharge) VALUES (8,9,1,'Fluorescent Light Bulbs',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Bring up to 10 fluorescent tubes and CFLs per a trip to a household hazardous waste facility or to a LightRecycle location. No fee.',NULL,NULL,NULL,NULL,'Fluorescent tubes and CFLs are banned from the garbage because they contain mercury, which is highly toxic. To prevent breaking the tubes or bulbs and releasing mercury when storing or transporting, wrap them in newspaper or put back in their original packaging.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
